Vue.component("app-case",{props:{identifier:{type:String,default:helpers.getAppID()}},data:function(){return{pdf_content:[]}},methods:{get_text(sr){try{const score=sr.calculation.another_calculation.score;var txt=(txt=this.$store.state.patient.data.extras.anrede+" ")+"weist im CASE vom "+formatDateCH(sr.date)+" einen Summenwert von "+score+" auf. ";if(score>=15)txt=txt+"Eine stationäre Therapie ist somit indiziert.";else txt=txt+"Eine stationäre Therapie ist ab 15 Punkten indiziert.";return txt}catch(e){return e}}},computed:{patient_secure(){try{return this.$store.state.patient.data.extras.secure}catch(e){return""}},sr_count_text(){try{var ret_text="";return 0===this.sr_data.length&&(ret_text="Keine Erfassung"),1===this.sr_data.length&&(ret_text="Eine Erfassung"),this.sr_data.length>1&&(ret_text="Erfassungen ("+this.sr_data.length+")"),ret_text}catch(e){return""}}},template:'\n        <div>\n            <div v-for="sr in sr_data" :key="sr.event_id">\n                <div v-if="sr.calculation_found">\n                    <optinomic-content-block :subtitle="sr_count_text" title="CASE" \n                        :id="\'id_erfassung_\' + sr.event_id">\n                        <p class="overline">Auswertung / Interpretation</p>\n                        <optinomic-clipboard-text :text="get_text(sr)">\n                        </optinomic-clipboard-text>\n                    </optinomic-content-block>\n                </div>\n            </div>\n        </div>\n    '});