Vue.component("app-pdf-druckvorlagen",{props:{identifier:{type:String,default:helpers.getAppID()}},created(){},data:function(){return{pdf_einladung_pa:{name:"Patienten-Assessment",title:"Einladung",version:"1.1",description:"Drucken der Zugangsdaten sowie einer Kurzeinführung für das Optinomic Patienten-Assessment.",source:{text_1:"Sie finden auf unserer Fragebogen-Plattform einige Fragebögen, in denen Sie Aussagen zu verschiedenen Themen und Zeiträumen einschätzen sollen. Am Anfang jedes Fragebogens finden Sie eine kurze Anleitung. Lesen Sie diese bitte sorgfältig durch.Achten Sie dabei auf die hervorgehobenen Angaben zu den Zeiträumen, auf die sich die Fragen und Aussagen beziehen. Diese können von Fragebogen zu Fragebogen unterschiedlich sein.",text_2:"Alle Fragebögen enthalten Aussagen. Ihre Aufgabe ist zu bewerten, inwieweit diese Aussagen auf Sie bzw. Ihre Situation zutreffen. Antworten Sie möglichst spontan – es gibt keine richtigen oder falschen Antworten. Wichtig ist, dass die jeweilige Antwort für Sie persönlich stimmt.",text_3:"Wir bitten Sie, die aufgeführten Fragebögen in der bestehenden Reihenfolge lückenlos zu bearbeiten. Zum starten JEDES EINZELNEN Fragebogens klicken Sie am rechten Rand des angegebenen Fragebogens auf «START». Wenn Sie einen Fragebogen fertig bearbeitet und abgeschickt haben, schliessen sie den entsprechenden Tab und gelangen somit wieder auf die Startseite.",text_4:"Falls Sie Fragen nicht verstehen oder etwas unklar ist, wenden Sie sich an die anwesende Betreuungsperson."},loading_string:"",content:null},pdf_notizblatt:{name:"Notizblatt",title:"Gesprächsnotiz",version:"1.0",description:"Drucken eines leeren Verlaufsblattes.",loading_string:"",content:null}}},methods:{},computed:{login_pid(){try{return this.patient_data.cis_pid}catch(err){return console.error("login_pid",err),"Error"}},login_pw(){try{return this.patient_data.birthdate.substring(0,10).replace("-","").replace("-","")}catch(err){return console.error("login_pw",err),"Error"}},pdf_ready(){try{if(this.patient_data){var credentials={table:{widths:[60,"*"],body:[[{text:"Login",color:"grey",margin:[0,6,0,6]},{text:this.login_pid,fontSize:16,margin:[0,6,0,6]}],[{text:"Passwort",color:"grey",margin:[0,6,0,6]},{text:this.login_pw,fontSize:16,margin:[0,6,0,6]}]]},layout:"noBorders"},_pdf_content=[];_pdf_content.push(makepdf._suedhang_logo_anschrift()),_pdf_content.push(makepdf._title(this.pdf_einladung_pa.name,this.patient_data.extras.full_name)),_pdf_content.push(makepdf._text(this.pdf_einladung_pa.source.text_1)),_pdf_content.push(makepdf._text(this.pdf_einladung_pa.source.text_2)),_pdf_content.push(makepdf._text(this.pdf_einladung_pa.source.text_3)),_pdf_content.push(makepdf._text(this.pdf_einladung_pa.source.text_4)),_pdf_content.push(makepdf._horizontalLine(62,"#F5F5F5")),_pdf_content.push(makepdf._heading("Persönliche Zugangsdaten",null,"h1")),_pdf_content.push(credentials),_pdf_content.push(makepdf._horizontalLine(62,"#F5F5F5")),this.pdf_einladung_pa.content=_pdf_content.slice();return(_pdf_content=[]).push({margin:[0,0,0,0],canvas:[{type:"line",x1:85,y1:0,x2:85,y2:720,lineWidth:.5,lineColor:"#BDBDBD"}]}),this.pdf_notizblatt.content=_pdf_content.slice(),!0}return!1}catch(e){return!1}}},template:'\n        <div>\n\n            <optinomic-content-block v-if="pdf_ready" title="Druckvorlagen" subtitle="PDF" id="pdf_druckvorlagen">\n                <optinomic-pdfmake :header-left="patient_data.extras.full_name"\n                    :footer-left="pdf_einladung_pa.title + \' :: \' + pdf_einladung_pa.name" header-right="Klinik Südhang"\n                    :document-title="pdf_einladung_pa.title + \' - \' + pdf_einladung_pa.name" :content="pdf_einladung_pa.content"\n                    hide-logo>\n                </optinomic-pdfmake>\n                <optinomic-pdfmake :header-left="patient_data.extras.full_name"\n                    :footer-left="pdf_notizblatt.title + \' :: \' + pdf_notizblatt.name" header-right="Klinik Südhang"\n                    :document-title="pdf_notizblatt.title" :content="pdf_notizblatt.content"\n                    hide-logo>\n                </optinomic-pdfmake>\n            </optinomic-content-block>\n\n        </div>\n    '});