Vue.component("app-data",{props:{identifier:{type:String,default:helpers.getAppID()}},created(){},computed:{app_loading(){try{return!this.$store.state.data_apps.data_object[this.identifier]}catch(e){return!0}},app_have_data(){try{return this.$store.state.data_apps.data_object[this.identifier].data.length>0}catch(e){return!1}}},methods:{},template:'\n        <template>\n            <div v-if="app_loading">\n                <v-skeleton-loader\n                    class="mx-auto"\n                    type="card"\n                ></v-skeleton-loader>\n            </div>\n            <div v-else>\n                <div v-if="app_have_data">\n                    <slot></slot>    \n                </div>\n                <div v-else>\n                    <v-alert prominent text type="info">\n                        Es sind keine Messdaten vorhanden.\n                    </v-alert>\n                </div>\n            </div>\n        </template>\n    '});