const global_helpers={install(Vue,options){Vue.mixin({methods:{getDateCH:function(date,full){var dateObj=new Date(date),o={};return o=!0===full?{weekday:"long",year:"numeric",month:"long",day:"numeric"}:{year:"numeric",month:"numeric",day:"numeric"},dateObj.toLocaleDateString("de-DE",o)}},computed:{missings(){try{if(null===this.$store.state.sr)return!1;var data_errors=!1;return this.$store.state.sr.data.forEach(function(item){!1===item.all_found&&(data_errors=!0)}.bind(this)),data_errors}catch(e){return!1}}}})}};Vue.use(global_helpers);