const pdf_globals={install(Vue,options){Vue.mixin({methods:{pdf_app_title:function(title,subtitle,description){var titel=[];return titel.push(makepdf._horizontalLine(100,"#E0E0E0")),titel.push(makepdf._heading(title,subtitle,"h1")),null!==description&&""!==description&&titel.push(makepdf._text(description)),makepdf._keepTogether(titel,title+"_titel")},pdf_no_data:function(title){var pdf=[];return pdf.push(makepdf._noData(title+": ","Keine Daten vorhanden.")),makepdf._keepTogether(pdf,title+"_no_data")},pdf_error:function(title){var pdf=[];return pdf.push(makepdf._noData(title+": ","Unerwarteter Fehler.")),makepdf._keepTogether(pdf,title+"_pdf_error")},pdf_app_info:function(m,big){var pdf=[];return!0===big?(pdf.push(makepdf._title(m.name,m.short_description)),pdf.push(makepdf._text(m.description)),makepdf._keepTogether(pdf,m.name+"_appinfo_big")):(pdf.push(this.pdf_app_title(m.name,m.short_description,m.description)),makepdf._keepTogether(pdf,m.name+"_appinfo"))}}})}};Vue.use(pdf_globals);